{"changed":false,"filter":false,"title":"IngresoController.php","tooltip":"/app/Http/Controllers/IngresoController.php","value":"<?php\n\nnamespace sisVentas\\Http\\Controllers;\n\nuse Illuminate\\Http\\Request;\n\nuse sisVentas\\Http\\Requests;\nuse Illuminate\\Support\\Facades\\Redirect;\nuse Illuminate\\Support\\Facades\\Input;\nuse sisVentas\\Http\\Requests\\IngresoFormRequest;\nuse sisVentas\\Ingreso;\nuse sisVentas\\DetalleIngreso;\nuse DB;\n\nuse Carbon\\Carbon;\nuse Response;\nuse Illuminate\\Support\\Collection;\n\nclass IngresoController extends Controller\n{\n    public function __construct()\n    {\n        $this->middleware('auth');\n    }    \n    \n    public function index(Request $request)\n    {\n        if ($request)\n        {\n            $query=trim($request->get('searchText'));\n            $ingresos=DB::table('ingreso as i')\n            ->join('persona as p', 'i.idproveedor', '=','p.idpersona')\n            ->join('detalle_ingreso as di', 'i.idingreso','=','di.idingreso')\n            ->select('i.idingreso', 'i.fecha_hora', 'p.nombre', 'i.tipo_comprobante','i.serie_comprobante', 'i.num_comprobante',\n            'i.impuesto', 'i.estado',DB::raw('sum(di.cantidad*precio_compra)as total' ))\n            ->where('i.num_comprobante','LIKE', '%'.$query.'%')\n            ->orderBy('i.idingreso', 'desc')\n            ->groupBy('i.idingreso', 'i.fecha_hora', 'p.nombre', 'i.tipo_comprobante','i.serie_comprobante', 'i.num_comprobante',\n            'i.impuesto', 'i.estado')\n            ->paginate(7);\n            return view('compras.ingreso.index',[\"ingresos\"=>$ingresos,\"searchText\"=>$query]);\n\n        }\n    }\n    public function create()\n\n    {\n        \n\n        $personas=DB::table('persona')->where('tipo_persona','=','Proveedor')->get();\n\n        $articulos=DB::table('articulo as art')\n            ->select(DB::raw('CONCAT(art.codigo, \" \", art.nombre) AS articulo'), 'art.idarticulo')\n            ->where('art.estado', '=', 'Activo')\n            ->get();\n            \n\n        return view(\"compras.ingreso.create\", [\"personas\"=>$personas, \"articulos\"=>$articulos]);\n    }\n    \n    public function store(IngresoFormRequest $request)\n    {\n        try{\n            DB::beginTransaction();\n            $ingreso=new Ingreso;\n            $ingreso->idproveedor=$request->get('idproveedor');\n            $ingreso->tipo_comprobante=$request->get('tipo_comprobante');\n            $ingreso->serie_comprobante=$request->get('serie_comprobante');\n            $ingreso->num_comprobante=$request->get('num_comprobante');              \n            \n            // $mytime->Carbon::now();\n            $ingreso->impuesto='18';\n            $ingreso->estado=('A'); \n            $ingreso->save();\n            \n            $idarticulo = $request->get('idarticulo');\n            $cantidad = $request->get('cantidad');\n            $precio_compra = $request->get('precio_compra');\n            $precio_venta= $request->get('precio_venta'); \n            \n            $cont=0;\n            \n            while($cont < count($idarticulo)){\n                $detalle = new DetalleIngreso();\n                $detalle->idingreso=$ingreso->idingreso;\n                $detalle->idarticulo=$idarticulo[$cont];\n                $detalle->cantidad=$cantidad[$cont];\n                $detalle->precio_compra=$precio_compra[$cont];\n                $detalle->precio_venta=$precio_venta[$cont];                \n                $detalle->save();\n                $cont=$cont+1;\n            }\n            \n            DB::commit();\n            \n        }catch(Exception $e)\n        {\n            DB::rollback();            \n        }\n        \n        return Redirect::to('compras/ingreso');\n        \n\n    }\n    \n    public function show($id){\n  \n        \n        $ingreso=DB::table('ingreso as i')\n            ->join('persona as p', 'i.idproveedor', '=','p.idpersona')\n            ->join('detalle_ingreso as di', 'i.idingreso','=','di.idingreso')\n            ->select('i.idingreso', 'i.fecha_hora', 'p.nombre', 'i.tipo_comprobante','i.serie_comprobante', 'i.num_comprobante','i.impuesto', 'i.estado',DB::raw('sum(di.cantidad*precio_compra) as total' ))\n            ->where('i.idingreso', '=',$id)\n            ->first(); \n\n        \n        $detalles=DB::table('detalle_ingreso as d')\n            ->join('articulo as a', 'd.idarticulo', '=', 'a.idarticulo')\n            \n            ->select('a.nombre as articulo', 'd.cantidad', 'd.precio_compra', 'd.precio_venta')\n            ->where('d.idingreso', '=',$id)\n            ->get();\n    \n            return view(\"compras.ingreso.show\", [\"ingreso\"=>$ingreso, \"detalles\"=>$detalles]);\n    }\n    \n    public function destroy($id)\n    {\n        $ingreso=Ingreso::findOrFail($id);\n        $ingreso->Estado='C';\n        $ingreso->update();\n        return Redirect::to('compras/ingreso');\n    }\n    \n}\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":951,"scrollleft":0,"selection":{"start":{"row":22,"column":34},"end":{"row":22,"column":34},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":52,"state":"php-start","mode":"ace/mode/php"}},"timestamp":1485257600848}